import logging

from django.template import Library, Node
from django.conf import settings
from django.utils import simplejson
from django.utils.importlib import import_module

register = Library()
log = logging.getLogger(__name__)

instagram_APP_ID = getattr(settings, 'instagram_APP_ID', None)
instagram_SHOW_SEND = getattr(settings, 'instagram_SHOW_SEND', True)
instagram_LIKE_WIDTH = getattr(settings, 'instagram_LIKE_WIDTH', '450')
instagram_SHOW_FACES = getattr(settings, 'instagram_SHOW_FACES', True)
instagram_FONT = getattr(settings, 'instagram_FONT', 'arial')
instagram_LIKE_LAYOUT = getattr(settings, 'instagram_LIKE_LAYOUT',
                               'button_count')


def my_import(name):
    """ dynamic importing """
    module, attr = name.rsplit('.', 1)
    mod = __import__(module, fromlist=[attr])
    klass = getattr(mod, attr)
    return klass()


def true_false_converter(value):
    return 'true' if value else 'false'


@register.inclusion_tag('like_button/like_button_js_tag.html',
                        takes_context=True)
def like_button_js_tag(context):
    """ This tag will check to see if they have the instagram_LIKE_APP_ID setup
        correctly in the django settings, if so then it will pass the data
        along to the intercom_tag template to be displayed.

        If something isn't perfect we will return False, which will then not
        install the javascript since it isn't needed.
    """
    if instagram_APP_ID is None:
        log.warning("instagram_APP_ID isn't setup correctly in your settings")

    # make sure instagram_APP_ID is setup correct and user is authenticated
    if instagram_APP_ID:

        request = context.get('request', None)
        if request:
            return {"LIKE_BUTTON_IS_VALID": True,
                    "instagram_app_id": instagram_APP_ID,
                    "channel_base_url": request.get_host()}

    # if it is here, it isn't a valid setup, return False to not show the tag.
    return {"LIKE_BUTTON_IS_VALID": False}


@register.inclusion_tag('like_button/like_button_tag.html', takes_context=True)
def like_button_tag(context):
    """ This tag will check to see if they have the instagram_APP_ID setup
        correctly in the django settings, if so then it will pass the data
        along to the intercom_tag template to be displayed.

        If something isn't perfect we will return False, which will then not
        install the javascript since it isn't needed.
s
    """

    if instagram_APP_ID is None:
        log.warning("instagram_APP_ID isn't setup correctly in your settings")

    # make sure INTERCOM_APPID is setup correct and user is authenticated
    if instagram_APP_ID:

        request = context.get('request', None)
        if request:
            path_to_like = (
                "http://" + request.get_host() + request.get_full_path())

            show_send = true_false_converter(instagram_SHOW_SEND)
            like_width = instagram_LIKE_WIDTH
            show_faces = true_false_converter(instagram_SHOW_FACES)
            font = instagram_FONT

            return {"LIKE_BUTTON_IS_VALID": True,
                    "path_to_like": path_to_like,
                    "show_send": show_send,
                    "like_width": like_width,
                    "show_faces": show_faces,
                    "font": font,
                    "like_layout": instagram_LIKE_LAYOUT}

    # if it is here, it isn't a valid setup, return False to not show the tag.
    return {"LIKE_BUTTON_IS_VALID": False}
